            'cheese': 12.00,
            'bread': 5.00,
            'wine': 10.00
          }
        }
      }
    }
  },
  methods: {
    handle_accept: function (event) {
      if (event) {
        this.reciepts[event.target.id].new = false
        this.reciepts[event.target.id].accepted = true
      }
    },
    handle_decline: function (event) {
      if (event) {
        this.reciepts[event.target.id].new = false
        this.reciepts[event.target.id].accepted = false
      }
    },
    edit: function (event) {
      if (event) {
      }
    },
    toggle: function (event) {
      if (event) {
        console.log('toggle ' + this.selected + ' ' + event.target.id)

        if (event.target.id === this.selected) {
          this.selected = null
        } else if (this.selected === null) {
          this.selected = event.target.id
        } else {
          this.selected = event.target.id
        }
      }
    }
  }
}
