<style>

body {
  margin: 0;
}

#app {
  font-family: 'Avenir', Helvetica, Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-align: center;
  color: #2c3e50;
  /* margin-top: 60px; */
}

.cont {
  display: grid;
  grid-template-columns: 2fr 1fr;
  width: 100vw;
  height: 100vh;
  text-align: left;
}

.left, .right {
  max-height: 100%;
  max-width: 100%;
  padding: 50px;
}

.left {
  background-color: #eee;
}

.right {
  background-color: #e7e7e7;
}

.reimbursment-title, .settings-title {
  font-size: 1.5em;
  font-weight: bold;
  margin: 10px 0;
}

.card {
  padding: 1em;
  height: 145px;
  transition: all 0.4s cubic-bezier(0.55, 0.06, 0.68, 0.19);
  margin: 10px;
  overflow-y: hidden;
  width: 500px;
}

.card_truncated {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
}

.card-right {
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
}

.name, .amount, .description {
  font-size: 0.8em;
}

.accept {
  background-color: green;
  padding: 7px;
  border-radius: 7px;
}

.decline {
  background-color: red;
  padding: 7px;
  border-radius: 7px;
}

.accept, .decline {
  margin: 4px;
  font-size: 0.8em;
  color: white !important;
  text-decoration: none;
  cursor: pointer;
}

img.accepted, img.declined {
  max-height: 50px;
  width: 100%;
}

.edit {
  margin: 1em;
  padding: 0.4em 0.5em;
  border-radius: 7px;
  font-size: 0.8em;
  color: white !important;
  text-decoration: none;
  cursor: pointer;
  background: grey;
  font-size: 1em;
  display: block;
  width: 45px;
  font-weight: bold;
}

.open {
  transform: rotate(90deg);
}

.toggle_button {
  height: 50px;
  align-self: center;
  transition: all 0.2s cubic-bezier(0.45, 0.05, 0.55, 0.95);
  cursor: pointer;
}

.openCard {
  height: 250px;
  overflow-y: visible;
}

.fade-enter-active, .fade-leave-active {
  transition: opacity 1s;
  transition-delay: 0.4s;
}

.fade-enter, .fade-leave-to /* .fade-leave-active below version 2.1.8 */ {
  opacity: 0;
}

.data {
  margin-top: 15px;
  font-size: 14px;
}

.intro-text {
  font-size: 15px;
  margin: 10px 0;
  width: 500px;
}

.right-content {
  display: flex;
  flex-direction: column;
}

